import React,{Component} from "react";
import "./App.css"

class App extends Component {
  constructor(){
    super()
    this.state = { num:3 }
    this.add = this.add.bind(Component)
  }

  add(a){
    this.setState(
      {num: this.state.num +2 }
    )
  }

  render(){
    return (
      <div className="App">
        {this.state.num}
        <button id="btn" onClick={this.add}> ok </button>
      </div>
    );
  }
}

export default App;
