import React, { useState } from "react";
import Child from "./main/Child";

function Main() {
    //  useState doesn't work in class
    const [color,setColor] = useState("red")
    
    const handelColor = function(){
        setColor("green");
    }

    return(
    <>
        <p> color state is: <span style={{color:color}}> {color} </span></p>
        <Child a={handelColor}/>
    </>
    )
}

export default Main
