import React, {  useCallback, useState  } from "react";
import "./App.css";
import Player from "./Player";
import Table from "./table/Table";

function App(){
  const [dadosa_num_p, setDadosa_num_p] = useState({num:1, noba:true , _666:[]})
  const [dadosa_num_s, setDadosa_num_s] = useState({num:1, noba:false, _666:[]})  

  const [p_yakliki,setP_yakliki] = useState(false)
  const [s_yakliki,setS_yakliki] = useState(false)

  const [wlad_p,setWlad_p] = useState(["block","block","block","block"]) 
  const [wlad_s,setWlad_s] = useState(["block","block","block","block"])

  const [boy_p,setBoy_p] = useState({boy1:0,boy2:0,boy3:0,boy4:0})
  const [boy_s,setBoy_s] = useState({boy1:0,boy2:0,boy3:0,boy4:0})

  const random=(dadosa,setDadosa_num,setDadosa_num_auther,wlad,setWlad,boy,setBoy,setYakliki,setYakliki_auther,ps)=>{  
    function tkhraj(boy,ps){
      let retur = true
      if( ps==="P" && ((boy.boy1===5 && boy.boy2===5) || (boy.boy1===5 && boy.boy3===5) || (boy.boy1===5 && boy.boy4===5) || (boy.boy2===5 && boy.boy3===5) || (boy.boy2===5 && boy.boy4===5) || (boy.boy3===5 && boy.boy4===5)) ){ retur = false}
      else if( ps==="S" && ((boy.boy1===39 && boy.boy2===39) || (boy.boy1===39 && boy.boy3===39) || (boy.boy1===39 && boy.boy4===39) || (boy.boy2===39 && boy.boy3===39) || (boy.boy2===39 && boy.boy4===39) || (boy.boy3===39 && boy.boy4===39)) ){ retur = false}
      return retur ;
    }

    if( dadosa.noba ){
      let random = Math.floor(Math.random()*(7 - 1))+1
      setDadosa_num( prevState =>({ ...prevState, num: random }) )
      // tadkhal
      if(random===5 && (wlad[0]==="block"||wlad[1]==="block"||wlad[2]==="block"||wlad[3]==="block") && tkhraj(boy,ps)  ){
        for( let i = 1 ; i < 4 ; i++ ){
          if( wlad[i] === "block" ){ 
            const arr = wlad.map(e => e);  
            arr[i]="none";  
            setWlad(arr); 
            if( ps === "P"){ 
              if( i===1 ){ setBoy( prevState => ({...prevState, boy1:5 })  ) }
              if( i===2 ){ setBoy( prevState => ({...prevState, boy2:5 })  ) }
              if( i===3 ){ setBoy( prevState => ({...prevState, boy3:5 })  ) }
              if( i===4 ){ setBoy( prevState => ({...prevState, boy4:5 })  ) }
            }else if( ps === "S"){ 
              if( i===1 ){ setBoy( prevState => ({...prevState, boy1:39 })  ) }
              if( i===2 ){ setBoy( prevState => ({...prevState, boy2:39 })  ) }
              if( i===3 ){ setBoy( prevState => ({...prevState, boy3:39 })  ) }
              if( i===4 ){ setBoy( prevState => ({...prevState, boy4:39 })  ) }
            } 
            break;
          }
        }
        setYakliki(false);
        setYakliki_auther(false);
        setDadosa_num(prevState => ({...prevState, noba: false}));
        setDadosa_num_auther(prevState => ({...prevState, noba: true}));
      }
      // makayan mayal3ab
      else if(random !== 5 && (wlad[0]==="block"&&wlad[1]==="block"&&wlad[2]==="block"&&wlad[3]==="block") ){
        setDadosa_num( prevState => ({...prevState, noba:false }) )
        setDadosa_num_auther( prevState => ({...prevState, noba:true }) )
      }
      // 6 3 ta3 marat 
      else if(random === 6 && dadosa._666.length === 2){
        setDadosa_num( prevState => ({...prevState, noba:false }) )
        setDadosa_num_auther( prevState => ({...prevState, noba:true }) )
        setDadosa_num( prevState => ({...prevState,  _666: [] }) )
        setYakliki(false);
        setYakliki_auther(false);
      }
      // ila jabti 6
      else if(random === 6 ){
        setDadosa_num( prevState => ({ ...prevState, _666:[...prevState._666 , true] }) )
        setDadosa_num( prevState => ({...prevState, noba:false }) )
        setDadosa_num_auther( prevState => ({...prevState, noba:false }) )
        setYakliki(true);
        setYakliki_auther(false);
      }
      // ra9m 3adi 
      else{
        setDadosa_num(prevState => ({...prevState, noba: false}))
        setDadosa_num_auther(prevState => ({...prevState, noba: false}))
        setDadosa_num( prevState => ({...prevState,  _666: [] }) )
        setYakliki(true);
        setYakliki_auther(false);
      }
    }
  }

  const dadosa_num_p_random = useCallback(() => {
    random(dadosa_num_p,setDadosa_num_p,setDadosa_num_s,wlad_p,setWlad_p,boy_p,setBoy_p,setP_yakliki,setS_yakliki,"P")  
  } );

  const dadosa_num_s_random = useCallback(() => {
    random(dadosa_num_s,setDadosa_num_s,setDadosa_num_p,wlad_s,setWlad_s,boy_s,setBoy_s,setS_yakliki,setP_yakliki,"S")
  } );

  const calcule =(key)=> {
    switch (key){
      case "boy_p_1" : setBoy_p( prevState => ({...prevState,  boy1: prevState.boy1 + dadosa_num_p.num  }) );
        (dadosa_num_p._666.length === 0)?setDadosa_num_s(prevState => ({...prevState, noba: true})):setDadosa_num_p(prevState => ({...prevState, noba: true}))
      break;
      case "boy_p_2" : setBoy_p( prevState => ({...prevState,  boy2: prevState.boy1 + dadosa_num_p.num  }) );
        setDadosa_num_s(prevState => ({...prevState, noba: true}));
      break;
      case "boy_p_3" : setBoy_p( prevState => ({...prevState,  boy3: prevState.boy1 + dadosa_num_p.num  }) );
        setDadosa_num_s(prevState => ({...prevState, noba: true}));
      break;
      case "boy_p_4" : setBoy_p( prevState => ({...prevState,  boy4: prevState.boy1 + dadosa_num_p.num  }) );
        setDadosa_num_s(prevState => ({...prevState, noba: true}));
      break;
      case "boy_s_1" : setBoy_s( prevState => ({...prevState,  boy1: prevState.boy1 + dadosa_num_s.num  }) );
        setDadosa_num_p(prevState => ({...prevState, noba: true}));
      break;
      case "boy_s_2" : setBoy_s( prevState => ({...prevState,  boy2: prevState.boy1 + dadosa_num_s.num  }) );
        setDadosa_num_p(prevState => ({...prevState, noba: true}));
      break;
      case "boy_s_3" : setBoy_s( prevState => ({...prevState,  boy3: prevState.boy1 + dadosa_num_s.num  }) );
        setDadosa_num_p(prevState => ({...prevState, noba: true}));
      break;
      case "boy_s_4" : setBoy_s( prevState => ({...prevState,  boy4: prevState.boy1 + dadosa_num_s.num  }) );
        setDadosa_num_p(prevState => ({...prevState, noba: true}));break;
    }
  }

  return( 
    <>
    {console.log(boy_p)}
    {console.log(boy_s)}
    <main style={{width:"100%"}} className="position-relative d-flex justify-content-center align-items-center">
      <Player bg='var(--primary)' num={dadosa_num_p.num} click={dadosa_num_p_random} />
      <Player bg='var(--danger)'  />
      <Player bg='var(--success)' num={dadosa_num_s.num} click={dadosa_num_s_random} />
      <Player bg='var(--warning)' />
      <div className="w-100 d-flex justify-content-center align-items-center" style={{height:"100vh"}}>    
        <Table 
          // dar
          wlad_p={wlad_p}
          wlad_s={wlad_s}

          // la3b
          boy_p={boy_p}
          boy_s={boy_s}

          // yakliki
          p_yakliki={p_yakliki}
          s_yakliki={s_yakliki}
          calcule={calcule}
        />
      </div>
    </main>
  </>
  )
}

export default App;
