import React , {Component} from "react";
import "./App.css";
import Items from "./Items";

class App extends Component { 
  constructor(){
    super();
    this.state ={
      styling : {}
    }
  }

  change_style =()=> {
    this.setState({
        styling:  {backgroundColor:"red",fontSize:"9rem"}
    })
  }

  render(){
    return (
      <div className="App" style={this.state.styling}> 
        <Items />
      </div>
    );
  }
}

export default App;
