import React , {Component} from "react";
import "./App.css"

class App extends Component { 
  constructor(){
    super();
    this.state = {
      email : "",
      password : "",
      style: {
        border:"2px solid red",
        padding:"0.3rem",
        marginTop:"2rem"
      },
    }
  }


  changeInput =(e) =>{
    this.setState({
      email:  e.target.value 
    })
    if(this.state.email.includes("@gmail.com")){
      this.setState({
        style : { border:"2px solid green"}
      })
    }
  }

  submitEvent =(e)=>{
    if(this.state.email.includes("@gmail.com")){
      alert("email Are true");
    }else{
      e.preventDefault();
    }
  }

  render(){
    return (
      <div className="App"> 
        <form action="test.php" onSubmit={this.submitEvent}>
          <input type="email" name="email" onChange={this.changeInput}/>
          <button type="submit"> submit </button>
          <div style={this.state.style}>
            {this.state.email}
          </div>
        </form>
      </div>
    );
  }
}

export default App;
