import React, {  useCallback, useState  } from "react";
import "./App.css";
import Player from "./Player";
import Table from "./table/Table";

function App(){
  const [dadosa_num_p, setDadosa_num_p] = useState({num:1, noba:true , _666:[]})
  const [dadosa_num_s, setDadosa_num_s] = useState({num:1, noba:false, _666:[]})
  [wlad_p,setWlad_p] = useState([true,true,true,true]) 
  [wlad_s,setWlad_s] = useState([true,true,true,true]) 
  if( this.props.p.num === 6 && this.props.p.noba === true ){
    console.log(`Tableau Primary ghay7arak ghayzid ${this.props.p.num} and _666 : ${this.props.p._666.length}`)   
  }else if( this.props.s.num === 6 && this.props.s.noba === true ){
    console.log(`Tableau Success ghay7arak  ${this.props.s.num} and _666 : ${this.props.s._666.length}`)   
  }else if( this.props.p.num !== 6 && this.props.p.noba === false ){
    console.log(`Tableau Primary ghay7arak ${this.props.p.num} and _666 : ${this.props.p._666.length}`)  
    if (this.props.p.num === 5){
        if(this.state.wlad_p[0] ){ 
            this.setState({wlad_p: [false,true,true,true]})
        }else if(this.state.wlad_p[1]){
            this.setState({wlad_p: [false,false,true,true]})
        }else if(this.state.wlad_p[2]){
            this.setState({wlad_p: [false,false,false,true]})
        }else if(this.state.wlad_p[3]){
            this.setState({wlad_p: [false,false,false,false]})
        }
    }
  }else if( this.props.s.num !== 6 && this.props.s.noba === false  ){
    console.log(`Tableau Success ghay7arak ${this.props.s.num} and _666 : ${this.props.s._666.length}`)  
  }


  const random =(noba,setDadosa_num,setDadosa_num_auther)=>{  
    if( noba ){
      let random = Math.floor(Math.random()*(7 - 1))+1
      setDadosa_num( prevState =>({...prevState, num: random }) )

      if(random === 6 ){
        setDadosa_num( prevState => ({...prevState, _666:[...prevState._666 , true] }) )
      }else{
        setDadosa_num(prevState => ({...prevState, noba: false}))
        setDadosa_num_auther(prevState => ({...prevState, noba: true}))
        setDadosa_num( prevState => ({...prevState,  _666: [] }) )
      }
    }
  }

  const dadosa_num_p_random = useCallback(() => {
    random(dadosa_num_p.noba,setDadosa_num_p,setDadosa_num_s)
  },[dadosa_num_p] );


  const dadosa_num_s_random = useCallback(() => {
    random(dadosa_num_s.noba,setDadosa_num_s,setDadosa_num_p)
  },[dadosa_num_s] );

  return( 
  <> 
    <main style={{width:"100%"}} className="position-relative d-flex justify-content-center align-items-center">

      <Player bg='var(--primary)' num={dadosa_num_p.num} click={dadosa_num_p_random} />
      <Player bg='var(--danger)'  />
      <Player bg='var(--success)' num={dadosa_num_s.num} click={dadosa_num_s_random} />
      <Player bg='var(--warning)' />

      <Table 
        p={dadosa_num_p} 
        s={dadosa_num_s} 
      />
    </main>
  </>
  )
}

export default App;
