import React , {Component} from "react";
import "./App.css"
import Items from "./Items";
class App extends Component { 
  constructor(){
    super();
    this.state = {
      email : "",
      password : "",
      style: {border:"2px solid red"},
    }
  }

  changeInput =(e) =>{
    this.setState({
      email:  e.target.value 
    })
    if( e.target.value.includes("@gmail.com")){
      this.setState({
        style : { border:"2px solid green"}
      })
    }
  }

  submitEvent =(e)=>{
    if(this.state.email.includes("@gmail.com")){
      alert("email Are true");
    }else{
      e.preventDefault();
    }
  }

  render(){
    return (
      <div className="App"> 

        <Items />
      </div>
    );
  }
}

export default App;
