import React  from "react";
import One from "./One";
import Two from "./Two";
import Three from "./Three";
import Four from "./Four"
import Five from "./Five"
import Six from "./Six"


function Player(props){
    console.log(`${props.bg}`);
    let poT , poL;
    if(props.bg === "var(--primary)"){ poT="0%"; poL="0%";}
    else if(props.bg === "var(--danger)"){ poT="calc(100vh - var(--P150))"; poL="0px";}
    else if(props.bg === "var(--success)"){poT="calc(100vh - var(--P150))"; poL="calc(100% - var(--P150))";}
    else if(props.bg === "var(--warning)"){ poT="0%"; poL="calc(100% - var(--P150))"; }  
    
    let num;
    for(let i=1 ; i<=6 ; i++ ){
        if(props.num === i ) num = numbers[i-1]
    }
   
    

    return(
        <div className="Player_cadre bg-light position-absolute rounded border border-5 d-flex justify-content-center align-items-center"
        style={{top:poT, left:poL}}>
            <div onClick={props.click} style={{backgroundColor:props.bg}} className="Player_dadosa border rounded">
               {num}
            </div>
        </div>
    )
}

export default React.memo(Player);
