import React , {Component} from "react";
import "./App.css"

class Items extends Component { 
    constructor(){
        super()
        this.state ={
            styling : {}
        }
        this.change_style = this.change_style.bind(this)
    }
    change_style(){
        this.setState({
            styling:  {backgroundColor:"red",fontSize:"9rem"}
        })
    }
  
  render(){
    return (
        <button onClick={this.change_style}> ChangeStyle </button>
    );
  }
}

export default Items;
