import React , {Component} from "react";
import Items from "./Items";
import "./App.css";


class App extends Component { 
  constructor(){
    super();
    this.counter = 0 ;
    this.state = {
      arr : ["Reda","Aymane","Kamal","Achraf","Malika","Anas","Ilham"],
      newarr : [],
    }
    this.addName = this.addName.bind(this);
  }
  addName(){
    this.setState (e=>{
      if(this.state.arr.length > this.counter){
        this.state.newarr.push(this.state.arr[this.counter]);
        console.log(this.state.newarr)
        this.counter++;
      }
    }) 
  }
  mapping(arr){
    const info = arr.map( e => <Items items={e} />)
    console.log(info)
    return info;
  }
  render(){
    var info = this.mapping(this.state.newarr)
    return (
      <div className="App"> 
        <button onClick={this.addName}> Click </button>
        {info}
      </div>
    );
  }
}

export default App;
