import React  from "react";

function Player(props){
    let poT , poL;
    (props.position0 == "top")?  poT="0%" :  poT="calc(100% - 150px)"; 
    (props.position0 == "top")?  poL="0%" :  poL="calc(100% - 150px)"; 
  
    return(
        <div className="Player_cadre bg-light  rounded border border-5" style={{
            position: "absolute",
            top : poT,
            left: poL,
        }}>
            <div className="border border-primary rounded" style={{backgroundColor:props.bg}}>  </div>
        </div>
    )
}

export default Player;
