import React, { useState } from "react";
import "./App.css";
import Player from "./Player";
import Table from "./table/Table";

function App(){
  const [dadosa_num_p, setDadosa_num_p] = useState({num:1,bool:true})
  const [dadosa_num_s, setDadosa_num_s] = useState({num:1,bool:false})

  const dadosa_num_p_random =()=>{
    if(dadosa_num_p.bool){

      setDadosa_num_p(()=>Math.floor(Math.random() * (7 - 1)) + 1)

    }
  }
  const dadosa_num_s_random =()=>{
    setDadosa_num_s(Math.floor(Math.random() * (7 - 1)) + 1)
  }

  return(
  <> 
    <main style={{width:"100%"}} className="position-relative d-flex justify-content-center align-items-center">
      <Player bg='var(--primary)' num={dadosa_num_p.num} click={dadosa_num_p_random} />
      <Player bg='var(--danger)' />
      <Player bg='var(--success)' num={dadosa_num_s.num} click={dadosa_num_s_random} />
      <Player bg='var(--warning)' />
      <Table />
    </main>
  </>
  )
}

export default App;
