      }
      if(key === "boy_s_"+i){ 
        calculeColumnCase_s(boy_s[keys_s[i-1]],dadosa_num_s.num,fat68_s[keys_s[i-1]],'boy'+i,'boy'+i,dadosa_num_s._666.length,sad_p,sad_s) 
      }
    }
  }  

  const calculeColumnCase_p = (boy_num,dadosa_num,key_boy,key_flesh,_666,finSad_lakhor,boy_s,setBoy_s,finSad_nta) => {
      let bool ;
      (_666 === 0)? setDadosa_num_s({...dadosa_num_s, noba: true}) : setDadosa_num_p({...dadosa_num_p, noba: true}) ;
