import React, { useEffect, useState }  from "react";
import "./App.css";

function App(){  
  const [width,setWidth] = useState(window.innerWidth)

  useEffect(()=>{
    window.addEventListener("resize",function(){
      setWidth(window.innerWidth)
    },[])
  })

  return (
    <div className="App"> 
        <h2> {width} </h2>
    </div>
  );
}

export default App;
