import React, { useState } from "react";
import "./App.css";
import Player from "./Player";
import Table from "./table/Table";

function App(){
  const [dadosa_num_p, setDadosa_num_p,getDadosa_num_p] = useState({num:1,noba:true})
  const [dadosa_num_s, setDadosa_num_s,getDadosa_num_s] = useState({num:1,noba:false})

  const random =(noba,num,bg,setDadosa_num,setDadosa_num_auther)=>{
    if( noba ){
      setDadosa_num({ num: Math.floor(Math.random()*(7 - 1))+1 , noba: true })
      if( num === 6){
        setDadosa_num(prevState => ({...prevState,noba: true}))
      }else{
        setDadosa_num(prevState => ({...prevState,noba: false}))
        setDadosa_num_auther(prevState => ({...prevState,noba: true}))
      }
      console.log(`bg : ${bg} num  ${getDadosa_num_p()} App Component`)
   }
  }

  const dadosa_num_p_random = ()=> random(
    dadosa_num_p.noba,
    dadosa_num_p.num,"primary",setDadosa_num_p,setDadosa_num_s);
  const dadosa_num_s_random = ()=> random(
    dadosa_num_s.noba,
    dadosa_num_s.num,"success",setDadosa_num_s,setDadosa_num_p);

  return(
  <> 
    <main style={{width:"100%"}} className="position-relative d-flex justify-content-center align-items-center">
      <Player bg='var(--primary)' num={dadosa_num_p.num} click={dadosa_num_p_random} />
      <Player bg='var(--danger)' />
      <Player bg='var(--success)' num={dadosa_num_s.num} click={dadosa_num_s_random} />
      <Player bg='var(--warning)' />
      <Table />
    </main>
  </>
  )
}

export default App;
