import React  from "react";
import "./App.css";
import Aside from "./aside/Aside";
import Main from "./main/Main";


class App extends React.Component{    
  constructor(){
    super()
    this.state = {
      width:"40%"
    }
  }

  changeStyle=()=>{
    this.setState({
      width:"9%"
    })
  }

  render(){
    return (
      <div className="App"> 
        <Aside width={this.state.width}/>
        <Main changeStyle={this.changeStyle} />
      </div>
    );
  }
}


export default App;
