import React  from "react";

function Player(props){
    console.log(`position = ${props.positionD}`);
    let poT , poL;
    (props.positionD == "top")? poT="0%" : poT="calc(100% - 150px)" ; 
    (props.positionD == "top")? poL="0%" : poL="calc(100% - 150px)" ; 
    
    return(
        <div className="Player_cadre bg-light position-absolute rounded border border-5 d-flex justify-content-center align-items-center"
        style={{top:poT, left:poL}}>
            <div className="Player_dadosa border bg-anger rounded" style={{backgroundColor:props.bg}}>  </div>
        </div>
    )
}

export default React.memo(Player);
