import React, {useState} from "react";
import "./App.css";


function App(){  
  const [width,setWidth] = useState(window.innerWidth)
    return (
    <div className="App"> 
      <h2> {width} </h2>
      <button type="button" onClick={()=>setWidth("900")}> change width </button>
    </div>
  )
}

export default App;
