import React, { useState } from "react";
//import Main from "../main/Main";

function Aside(props){
    const [width,setWidth] = useState("40%") 
    
    const changeStyle =()=>{
        setWidth("5%")
    } 
    
    return(
        <>
            <aside style={{backgroundColor:"red", height:"90vh", width:width}} >
                <p style={{color:'white',fontSize:'9'}}> lorem test </p> 
            </aside>
        </>
    )
}
export default Aside
