import "./App.css";
import Aside from "./aside/Aside";
import Main from "./main/Main";


function App(){ 
  const [width,setWidth] = useState("40%")   

  const changeStyle =()=> {
    setWidth("5%");
  } 

    return (
      <div className="App"> 
        <Aside width={width}/>
        <Main changeStyle={changeStyle} />
      </div>
    );
}


export default App;
