import React , {Component} from "react";
import Items from "./Items";
import info from "./info";

import "./App.css";


class App extends Component { 
  constructor(){
    super();
    this.state = {
      counter: 0 ,
      arr : info ,
      newarr : []
    }
    this.addName = this.addName.bind(this);
  }
  addName(){
    if(this.state.arr.length > this.state.counter){
      //this.setState( e => {
      //    return {counter : e.counter + 1} 
      //})

      this.state.newarr.push(this.state.arr[this.state.counter])
    } 
  }
  mapping(arr){
    const info = arr.map( e => <Items items={e} />)
    return info;
  }
  render(){
    return (
      <div className="App"> 
        <button onClick={this.addName}> Click </button>
        {this.mapping(this.state.newarr)}
      </div>
    );
  }
}

export default App;
