import React , {Component} from "react";
import Items from "./Items";
import info from "./info";
import "./App.css";


class App extends Component { 
  constructor(){
    super();
    this.state = {
      counter: 0 ,
      arr : info ,
      newarr : {}
    }
    this.addName = this.addName.bind(this);
  }

  addName(){
    if(this.state.arr.length > this.state.counter){

      //this.state.newarr["name"] = this.state.arr[this.state.counter]
      this.state.newarr.push({a:this.state.arr[this.state.counter]})
      this.setState( e => {
        return {counter : e.counter + 1} 
    })
    } 
  }

  mapping(obj){
    const info = obj.map( e => <Items items={e.a} />)
    return info;
  }

  render(){
    return (
      <div className="App"> 
        <button onClick={this.addName} className="btn"> Click </button>
        {this.mapping(this.state.newarr.a)}
      </div>
    );
  }
}

export default App;
