function Aside(props){
    const [width,setWidth] = useState("40%") 
    const changeStyle =()=>{
        setWidth("5%")
    } 
    return(
        <>
            
            <aside style={{backgroundColor:"red", height:"90vh", width:width}}>

            </aside>
        </>
    )
}
export default Aside
