import React, {  useCallback , useState  } from "react";
import "./App.css";
import Player from "./Player";
import Table from "./table/Table";

function App(){
  const [dadosa_num_p, setDadosa_num_p] = useState({num:1, noba:true , _666:[]})
  const [dadosa_num_s, setDadosa_num_s] = useState({num:1, noba:false, _666:[]}) 

  const [p_yakliki,setP_yakliki] = useState(false)
  const [s_yakliki,setS_yakliki] = useState(false)

  const [wlad_p,setWlad_p] = useState(["block","block","block","block"]) 
  const [wlad_s,setWlad_s] = useState(["block","block","block","block"])

  const [boy_p,setBoy_p] = useState({boy1:0,boy2:0,boy3:0,boy4:0})
  const [boy_s,setBoy_s] = useState({boy1:0,boy2:0,boy3:0,boy4:0})

  const [fat68_s,setFat68_s] = useState({boy1:false,boy2:false,boy3:false,boy4:false})

  const [boy_flesh_p,setBoy_flesh_p] = useState({boy1:0,boy2:0,boy3:0,boy4:0})
  const [boy_flesh_s,setBoy_flesh_s] = useState({boy1:0,boy2:0,boy3:0,boy4:0})

  const fin_sad =(obj)=> {
    const arr = [] 
    if((obj.boy1 === obj.boy2 || obj.boy1 === obj.boy3 || obj.boy1 === obj.boy4) && obj.boy1 !== 0 && obj.boy1 !== true ) arr.push(obj.boy1)
    if((obj.boy2 === obj.boy3 || obj.boy2 === obj.boy4) && obj.boy2 !== 0 && obj.boy2 !== true )  arr.push(obj.boy2)
    if(obj.boy3 === obj.boy4 && obj.boy3 !== 0 && obj.boy3 !== true ) arr.push(obj.boy3)
    return arr
  }

  const random=(dadosa,setDadosa_num,setDadosa_num_auther,wlad,setWlad,boy,setBoy,setYakliki,setYakliki_auther,ps)=>{  
    function tkhraj(boy,ps){
      let retur = true , p = 0 , s = 0;
      for(let i in boy){
        if(boy[i] === 5  && ps==="P") p++;
        if(boy[i] === 39 && ps==="S") s++;
        if( p == 2 || s === 2 ){ retur = false; break; } 
      }
      return retur ;
    }

    if( dadosa.noba ){
      let random = 6 //Math.floor(Math.random()*(7 - 1))+1
      setDadosa_num( prevState =>({ ...prevState, num: random }) )
      if( random===5 && (wlad[0]==="block"||wlad[1]==="block"||wlad[2]==="block"||wlad[3]==="block") && tkhraj(boy,ps) ){
        const boy_key = ['boy1','boy2','boy3','boy4']
        for( let i = 0 ; i < 4 ; i++ ){
          if( wlad[i] === "block" ){ 
            const arr = wlad.map(e => e); arr[i]="none"; setWlad(arr); 
            (ps === "P")? setBoy( {...boy , [boy_key[i]]:5 }): setBoy( {...boy, [boy_key[i]]:39} );
            break;
          }
        }
        setDadosa_num(prevState => ({...prevState, noba: false}));
        setDadosa_num_auther(prevState => ({...prevState, noba: true}));
        setYakliki(false); setYakliki_auther(false);
      }else if(random === 6 && wlad[0]==="block" && wlad[1]==="block" && wlad[2]==="block" && wlad[3]==="block" ){
        setDadosa_num( prevState => ({...prevState, noba:true }) )
        setDadosa_num_auther( prevState => ({...prevState, noba:false }) )
        setYakliki(false); setYakliki_auther(false);
      }else if(random !== 5 && (wlad[0]==="block"&&wlad[1]==="block"&&wlad[2]==="block"&&wlad[3]==="block") ){
        setDadosa_num( prevState => ({...prevState, noba:false }) )
        setDadosa_num_auther( prevState => ({...prevState, noba:true }) )
      }else if(random === 6 && dadosa._666.length === 2){
        setDadosa_num( prevState => ({...prevState, noba:false }) )
        setDadosa_num_auther( prevState => ({...prevState, noba:true }) )
        setDadosa_num( prevState => ({...prevState,  _666: [] }) )
        setYakliki(false);setYakliki_auther(false);    
        //const newArr = []
        //for( let i in boy )  if( boy[i] !== 0 && boy[i] !== true ) newArr.push({ [i] : boy[i] })
        //for()
        var maxKey = boy.max(Object.keys(boy), o => boy[o]);
        console.log(maxKey)

      }else if(random === 6 ){
        setDadosa_num( prevState => ({ ...prevState, _666:[...prevState._666 , true] }) )
        setDadosa_num( prevState => ({...prevState, noba:false }) )
        setDadosa_num_auther( prevState => ({...prevState, noba:false }) )
        setYakliki(true);setYakliki_auther(false);
      }else{
        setDadosa_num(prevState => ({...prevState, noba: false}))
        setDadosa_num_auther(prevState => ({...prevState, noba: false}))
        setDadosa_num( prevState => ({...prevState,  _666: [] }) )
        setYakliki(true);setYakliki_auther(false);
      }
    }
  }

  const dadosa_p = useCallback(()=>{
    random(dadosa_num_p,setDadosa_num_p,setDadosa_num_s,wlad_p,setWlad_p,boy_p,setBoy_p,setP_yakliki,setS_yakliki,"P") 
  });  

  const dadosa_s = useCallback(() => {
    random(dadosa_num_s,setDadosa_num_s,setDadosa_num_p,wlad_s,setWlad_s,boy_s,setBoy_s,setS_yakliki,setP_yakliki,"S")
  });

  const calcule =(key)=> {
    const sad_p = fin_sad(boy_p);
    const sad_s = fin_sad(boy_s);
    const keys_p = Object.keys(boy_p);  
    const keys_s = Object.keys(boy_s);  
    for(let i=1 ; i<=4 ; i++){
      if(key === "boy_p_"+i){ calculeColumnCase_p(boy_p[keys_p[i-1]],dadosa_num_p.num,'boy'+i,'boy'+i,dadosa_num_p._666.length,sad_s,boy_s,setBoy_s,setWlad_s,wlad_s) }
      if(key === "boy_s_"+i){ calculeColumnCase_s(boy_s[keys_s[i-1]],dadosa_num_s.num,fat68_s[keys_s[i-1]],'boy'+i,'boy'+i,dadosa_num_s._666.length,sad_p) }
    }
  }

  const dead =(blasajdida,ar9am_obj,setar9am_obj,setWlad,wlad,setDadosa_num,dadosa_num,setNoba_auther,noba_auther_obj)=>{
    const keys = Object.keys(ar9am_obj)
    let wach_9tal = false ;
    for(let i=0 ; i < keys.length ; i++ ){
      if(ar9am_obj[keys[i]] === blasajdida && ar9am_obj[keys[i]] !== 5 && ar9am_obj[keys[i]] !== 12 && ar9am_obj[keys[i]] !== 17 && ar9am_obj[keys[i]] !== 22 && ar9am_obj[keys[i]] !== 29 && ar9am_obj[keys[i]] !== 34 && ar9am_obj[keys[i]] !== 39 && ar9am_obj[keys[i]] !== 46 && ar9am_obj[keys[i]] !== 51 && ar9am_obj[keys[i]] !== 56 && ar9am_obj[keys[i]] !== 63 && ar9am_obj[keys[i]] !== 68){
        setar9am_obj({...ar9am_obj , [keys[i]]:0 });   
        const arr = wlad.map(e => e); arr[i]="block"; setWlad(arr); 
        setDadosa_num({...dadosa_num , num:20})
        setNoba_auther({...noba_auther_obj ,noba:false})
        wach_9tal = true;   break;
      }
    }
    return wach_9tal
  }

  const sad_espaniol = (blasa9dima)=> {
    const keysP = Object.keys(boy_p);
    const keysS = Object.keys(boy_s);
    const arr_sadan = [];
    let min
    for(let i=0 ; i<4 ; i++){
      for(let j=0 ; j<4 ; j++){
        if( (boy_p[keysP[i]] === boy_s[keysS[j]]) && boy_s[keysS[j]] !== 0 && boy_s[keysS[j]] !== true ){
          arr_sadan.push(boy_p[keysP[i]])
        }
      } 
    }
    if( arr_sadan.length === 0 || (arr_sadan.length === 1 && (arr_sadan[0] <= blasa9dima)) ){
      min = 0
    }else if( arr_sadan.length === 1 ){
      min = arr_sadan[0]
    }else{
      const kbar = arr_sadan.filter( e =>{ if(e > blasa9dima ){return e} } )
      if(kbar.length === 0){
        min = 0 
      }else if(kbar.length === 1){
        min = kbar[0]
      }else{
        min =  kbar[0]
        for( let i=1 ; i<kbar.length ;i++){
          if(min > kbar[i]){
            min = kbar[i]
          }
        }
      }
    }
    return min;
  }


  const sad_espaniol_s = (blasa9dima,balasajdida,fat_68)=> {
    const keysP = Object.keys(boy_p);
    const keysS = Object.keys(boy_s);
    const arr_sadan = [];
    let bool = false
    for(let i=0 ; i<4 ; i++){
      for(let j=0 ; j<4 ; j++){
        if( (boy_p[keysP[i]] === boy_s[keysS[j]]) && boy_s[keysS[j]] !== 0 && boy_s[keysS[j]] !== true ){
          arr_sadan.push(boy_p[keysP[i]])
        }
      } 
    }
    if(arr_sadan.length === 1 && blasa9dima < arr_sadan[0] && fat_68 == false){
      bool = true 
    }else if(arr_sadan.length === 1 && blasa9dima > arr_sadan[0] && balasajdida >= arr_sadan[0] && fat_68 == false){
      bool = true
    }
    return bool;
  }


  const calculeColumnCase_s = (boy_num,dadosa_num,fat68_boy,boy_s_key,Fat68_key,_666,finSad_lakhor) => {
    (_666 === 0)? setDadosa_num_p({...dadosa_num_p, noba: true}) : setDadosa_num_s({...dadosa_num_s, noba: true})
    const blasaJdida = dadosa_num + boy_num

    if( blasaJdida > 68 && fat68_boy === false ){
        if(     sad_espaniol_s(boy_num,blasaJdida-68,false) === false              &&            ((finSad_lakhor.length === 0)  ||  (finSad_lakhor.length === 1 && ((boy_num > finSad_lakhor[0] && finSad_lakhor[0] > 39) || ((finSad_lakhor[0] >= 5 && finSad_lakhor[0] <=34) && (boy_num > 39 && blasaJdida-68 < finSad_lakhor[0]))))  ||  (finSad_lakhor.length === 2 &&  ( (boy_num > finSad_lakhor[0] && finSad_lakhor[0] > 39) || ((finSad_lakhor[0] >= 5 && finSad_lakhor[0] <=34)&&(boy_num > 39 && blasaJdida-68 < finSad_lakhor[0]))))  ||  (finSad_lakhor.length === 2 && ((boy_num > finSad_lakhor[1] && finSad_lakhor[1] > 39) || ((finSad_lakhor[1] >= 5 && finSad_lakhor[1] <=34)&&(boy_num > 39 && blasaJdida-68 < finSad_lakhor[1])))) )){
          (!dead(blasaJdida-68,boy_p,setBoy_p,setWlad_p,wlad_p,setDadosa_num_s,dadosa_num_s,setDadosa_num_p,dadosa_num_p)) ?
          setS_yakliki(false) : setS_yakliki(true) ;
          setBoy_s({...boy_s , [boy_s_key]: blasaJdida - 68 });
          setFat68_s({...fat68_s , [Fat68_key]: true });
        }else if(        sad_espaniol_s(boy_num,blasaJdida-68,false)               ||         ((finSad_lakhor.length === 1 && ((boy_num < finSad_lakhor[0] && finSad_lakhor[0] > 39) || ((finSad_lakhor[0] >= 5 && finSad_lakhor[0] <=34)&&(boy_num > 39 && blasaJdida-68 >= finSad_lakhor[0]))))  ||  (finSad_lakhor.length === 2 && ((boy_num < finSad_lakhor[0] && finSad_lakhor[0] > 39) || ((finSad_lakhor[0] >= 5 && finSad_lakhor[0] <=34)&&(finSad_lakhor[0] >= 5 && finSad_lakhor[0] <=34))))  ||  (finSad_lakhor.length === 2 && ((boy_num < finSad_lakhor[1] && finSad_lakhor[1] > 39) || ((finSad_lakhor[1] >= 5 && finSad_lakhor[1] <=34)&&(boy_num > 39 && blasaJdida-68 >= finSad_lakhor[1])))) )){    
          setDadosa_num_p({...dadosa_num_p , noba: false})
          setDadosa_num_s({...dadosa_num_s , noba: false})
        }
    }else if( (blasaJdida > 34 && fat68_boy) && (blasaJdida - 34 <= 8) ){
      if(           (sad_espaniol(boy_num) === 0 ||  blasaJdida <= sad_espaniol(boy_num))                  &&   ((finSad_lakhor.length === 0 || (finSad_lakhor.length === 1 && (finSad_lakhor[0] < 39 && finSad_lakhor[0] > 34))) || (finSad_lakhor.length === 1 && (boy_num > finSad_lakhor[0] || blasaJdida < finSad_lakhor[0])) || (finSad_lakhor.length === 2 && ((boy_num > finSad_lakhor[0] && boy_num > finSad_lakhor[1]) || (boy_num > finSad_lakhor[0] && boy_num < finSad_lakhor[1]) && (blasaJdida < finSad_lakhor[1]) || (boy_num < finSad_lakhor[0] && boy_num > finSad_lakhor[1]) && (blasaJdida < finSad_lakhor[0]) )) )){
        if(blasaJdida - 34 === 8){ 
          setDadosa_num_s({...dadosa_num_s, num:10});
          setDadosa_num_p({...dadosa_num_p, noba:false});
        }else{ 
          setS_yakliki(false);
        } 
        setBoy_s({...boy_s,  [boy_s_key]:true });
        setBoy_flesh_s({...boy_flesh_s , [Fat68_key]:blasaJdida - 34 });
      }else if(   blasaJdida > sad_espaniol(boy_num)                            ||  ((finSad_lakhor.length === 1 && blasaJdida >= finSad_lakhor[0]) || (finSad_lakhor.length === 2 && ((boy_num < finSad_lakhor[0] && boy_num < finSad_lakhor[1]) || ((boy_num > finSad_lakhor[0] && boy_num < finSad_lakhor[1]) && (blasaJdida >= finSad_lakhor[1])) || ((boy_num < finSad_lakhor[0] && boy_num > finSad_lakhor[1]) && (blasaJdida >= finSad_lakhor[0])))) )){
        setDadosa_num_p({...dadosa_num_p , noba:false })
        setDadosa_num_s({...dadosa_num_s , noba:false })
      }
    }else if ( (blasaJdida > 34 && fat68_boy) && (blasaJdida - 34 > 8) ){
      setDadosa_num_p({...dadosa_num_p , noba: false})
    }else {
      if( (sad_espaniol(boy_num) === 0 || blasaJdida < sad_espaniol(boy_num))              &&            ((finSad_lakhor.length === 0) || (finSad_lakhor.length === 1 && ((finSad_lakhor[0] > boy_num && finSad_lakhor[0] > blasaJdida ) ||  boy_num > finSad_lakhor[0])) || (finSad_lakhor.length === 2 && (boy_num > finSad_lakhor[0] && boy_num > finSad_lakhor[1]) || ((boy_num < finSad_lakhor[0] && boy_num < finSad_lakhor[1]) && (blasaJdida < finSad_lakhor[0] &&  blasaJdida < finSad_lakhor[1])) || ((boy_num > finSad_lakhor[0] && boy_num < finSad_lakhor[1]) && (blasaJdida < finSad_lakhor[1])) || ((boy_num < finSad_lakhor[0] && boy_num > finSad_lakhor[1]) && (blasaJdida < finSad_lakhor[0])) ) )){
        (!dead(blasaJdida,boy_p,setBoy_p,setWlad_p,wlad_p,setDadosa_num_s,dadosa_num_s,setDadosa_num_p,dadosa_num_p)) ?
        setS_yakliki(false) : setS_yakliki(true) ;
        setBoy_s({...boy_s,  [boy_s_key]: blasaJdida });
      }else if(           blasaJdida >= sad_espaniol(boy_num)           ||            ((finSad_lakhor.length === 1 && (boy_num  < finSad_lakhor[0] &&  blasaJdida >= finSad_lakhor[0])) || (finSad_lakhor.length === 2 &&  ((boy_num < finSad_lakhor[0] && boy_num < finSad_lakhor[1]) && (blasaJdida >= finSad_lakhor[0] ||  blasaJdida >= finSad_lakhor[1])) || ((boy_num > finSad_lakhor[0] && boy_num < finSad_lakhor[1]) && (blasaJdida >= finSad_lakhor[1])) || ((boy_num < finSad_lakhor[0] && boy_num > finSad_lakhor[1]) && (blasaJdida >= finSad_lakhor[0])) ))){
        setDadosa_num_p({...dadosa_num_p, noba: false})
        setDadosa_num_s({...dadosa_num_s, noba: false})
      }
    } 
  }

  const calculeColumnCase_p = (boy_num,dadosa_num,key_boy,key_flesh,_666,finSad_lakhor,boy_s,setBoy_s) => {
      (_666 === 0)? setDadosa_num_s({...dadosa_num_s, noba: true}) : setDadosa_num_p({...dadosa_num_p, noba: true}) ;
      const blasaJdida = boy_num + dadosa_num;
