import React , {Component} from "react";
import Items from "./Items";
import "./App.css"


class App extends Component{ 
  constructor(){
    super()
    this.counter = 0 ;
    this.state = {
      arr : ["Reda","Eskouni","Kamal","Achraf","Malika"],
      newarr : []
    }
    this.addName = this.addName.bind(this);
  }
  addName(){
    if(this.state.arr.length > this.counter){
      this.state.newarr.push(this.state.arr[this.counter]);
      this.counter++;
      console.log(this.counter );
    } 
  }
  mapping(arr){
    const info = arr.map( e =><Items items={e} />)
    return info;
  }
  render(){
    return (
      <div className="App"> 
        <button onClick={this.addName}> Click </button>
        {this.mapping(this.state.newarr)}
      </div>
    );
  }
}

export default App;
