import React, {  useCallback, useState  } from "react";
import "./App.css";
import Player from "./Player";
import Table from "./table/Table";

function App(){

  const [dadosa_num_p, setDadosa_num_p] = useState({num:1, noba:true , _666:[]})
  const [dadosa_num_s, setDadosa_num_s] = useState({num:1, noba:false, _666:[]})

  const [wlad_p,setWlad_p] = useState(["block","block","block","block"]) 
  const [wlad_s,setWlad_s] = useState(["block","block","block","block"])

  const [noba_table_p,setNoba_table_p] = useState()
  const [noba_table_s,setNoba_table_s] = useState()

  const [num_table,setNum_table] = useState() 

  const [boy_p,setBoy_p] = useState({boy1:0,boy2:0,boy3:0,boy4:0})
  const [boy_s,setBoy_s] = useState({boy1:0,boy2:0,boy3:0,boy4:0})

  const random=(dadosa,setDadosa_num,setDadosa_num_auther,wlad,setWlad,boy,setBoy,ps)=>{  
    if( dadosa.noba ){
      let random = Math.floor(Math.random()*(7 - 1))+1
      setDadosa_num( prevState =>({ ...prevState, num: random }) )
      setNum_table(random)
      if(random === 6 && dadosa._666.length === 2){
        setDadosa_num( prevState => ({...prevState, noba:false }) )
        setDadosa_num_auther( prevState => ({...prevState, noba:true }) )
        setDadosa_num( prevState => ({...prevState,  _666: [] }) )
      }else if(random === 6 ){
        setDadosa_num( prevState => ({ ...prevState, _666:[...prevState._666 , true] }) )
      }else if(random === 5 && (wlad[0]==="block" || wlad[1]==="block" || wlad[2]==="block" || wlad[3]==="block")){

      }
      else{
        setDadosa_num(prevState => ({...prevState, noba: false}))
        setDadosa_num_auther(prevState => ({...prevState, noba: true}))
        setDadosa_num( prevState => ({...prevState,  _666: [] }) )
      }
    }
  }

  const dadosa_num_p_random = useCallback(() => {
    random(dadosa_num_p,setDadosa_num_p,setDadosa_num_s,wlad_p,setWlad_p,boy_p,setBoy_p,"P")  
  } );

  const dadosa_num_s_random = useCallback(() => {
    random(dadosa_num_s,setDadosa_num_s,setDadosa_num_p,wlad_s,setWlad_s,boy_s,setBoy_s,"S")
  } );

  return( 
    <>
    {console.log(boy_p)}
    {console.log(boy_s)}
    <main style={{width:"100%"}} className="position-relative d-flex justify-content-center align-items-center">
      <Player bg='var(--primary)' num={dadosa_num_p.num} click={dadosa_num_p_random} />
      <Player bg='var(--danger)'  />
      <Player bg='var(--success)' num={dadosa_num_s.num} click={dadosa_num_s_random} />
      <Player bg='var(--warning)' />
      <div className="w-100 d-flex justify-content-center align-items-center" style={{height:"100vh"}}>    
        <Table 
          //dar
          wlad_p={wlad_p}
          wlad_s={wlad_s}

          //
          noba_p={noba_table_p}
          noba_s={noba_table_s}
          num={num_table}

          //
          boy_p={boy_p}
          boy_s={boy_s}
        />
      </div>
    </main>
  </>
  )
}

export default App;
